int	ft_atoi(const char *nptr)
{
	int	nb;
	int	i;
	int	t;

	nb = 0;
	i = 0;
	t = 1;
	while ((nptr[i] >= 9 && nptr[i] <= 13) || nptr[i] == 32)
		i++;
	if (nptr[i] == '+' || nptr[i] == '-')
	{
		if (nptr[i] == '-')
			t = -t;
		i++;
	}
	while ((nptr[i] <= '9' && nptr[i] >= '0'))
	{
		nb = ((nb * 10) + nptr[i] - '0');
		i++;
	}
	return (t * nb);
}
